import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import axios from 'axios';
import Cart from './pages/Cart';
import ServiceDetails from './pages/ServiceDetails';
import ChangePassword from './pages/ChangePassword';
import ProtectedRoute from "./components/ProtectedRoute";
import { ClipLoader } from 'react-spinners';

export const CartContext = React.createContext();

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      const savedUser = localStorage.getItem('user');
      const savedToken = localStorage.getItem('token');
      if (savedUser && savedToken) {
        setUser(JSON.parse(savedUser));
        setToken(savedToken);
      }
      setLoading(false);
    }, 1000);
  }, []);

  const saveAuth = (userData, jwt) => {
    setUser(userData);
    setToken(jwt);
    localStorage.setItem('user', JSON.stringify(userData));
    localStorage.setItem('token', jwt);
  };

  const logout = async () => {
    //const token = localStorage.getItem('accessToken');
    //const refresh_token = localStorage.getItem('refreshToken');

    try {
      console.log()
      await axios.post('http://localhost:8000/api/v1/logout', {}, {
        headers: {
          'refresh-token': localStorage.getItem('refreshToken')
        }
      });

      // Очищаем токен на клиенте
      localStorage.removeItem('accessToken');
      localStorage.removeItem('user');

      // Перенаправление или сообщение
      window.location.href = '/';
    } catch (error) {
      console.error('Ошибка при выходе:', error.response?.data || error.message);
    }
  };
  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen w-screen fixed top-0 left-0">
        <ClipLoader size={500} color={"#123abc"} loading={loading} />
      </div>
    );
  }

  return (
    <CartContext.Provider value={{ cartItems, setCartItems, user, token, saveAuth, logout }}>
      <Router>
        <Header />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/service/:id" element={<ServiceDetails />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} /> 
          <Route 
            path="/cart"
            element={<ProtectedRoute>
              <Cart />
            </ProtectedRoute>} />
          <Route 
            path="/change_password"
            element={<ProtectedRoute><ChangePassword /></ProtectedRoute>} />
        </Routes>
      </Router>
    </CartContext.Provider>
  );
}

export default App;
