// Обновленный фрагмент внутри src/pages/Home.js
import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../App';

const Home = () => {
  const [products, setProducts] = useState([]);
  const { user } = useContext(CartContext);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:8000/products')
      .then(response => setProducts(response.data))
      .catch(error => console.error('Ошибка при получении продуктов:', error));
  }, []);

  const addToCart = (product) => {
    if (!user) {
      alert('Пожалуйста, войдите в аккаунт для добавления в корзину');
      navigate('/login');
      return;
    }
    axios.post(`http://localhost:8000/basket/${user.id}`, product)
      .then(response => {
        alert(`Добавлено в корзину: ${product.name}`);
      })
      .catch(error => {
        alert(error.response.data.detail || 'Ошибка добавления в корзину');
      });
  };

  const goToDetails = (id) => {
    navigate(`/service/${id}`);
  };

  return (
    <div>
      {/* Остальной контент (Hero, About, Contact) без изменений */}
      <section className="services" id="services">
        <div className="container">
          <h2>Наши услуги</h2>
          <div className="service-grid">
            {products.map(product => (
              <div className="service-card" key={product.id}>
                <h3>{product.name}</h3>
                <p>{product.description.substring(0, 100)}...</p>
                <p className="price">Цена: ${product.price}/месяц</p>
                <button className="detail-btn" onClick={() => goToDetails(product.id)}>
                  Подробнее
                </button>
                <button className="detail-btn" onClick={() => addToCart(product)}>
                  В корзину
                </button>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
