import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { CartContext } from '../App';

const Cart = () => {
  const { user } = useContext(CartContext);
  const [basketItems, setBasketItems] = useState([]);

  const fetchBasket = async () => {
    try {
      const response = await axios.get('http://localhost:8000/basket', {
        headers: { Authorization: `Bearer ${localStorage.getItem('accessToken')}` }
      });
      //response.data.basket_item_id = localStorage.setItem('basket_id');

      setBasketItems(response.data);
    } catch (error) {
      console.error('Ошибка получения корзины:', error);
    }
  };

  useEffect(() => {
    fetchBasket();
  }, [user]);

  token = localStorage.getItem('accessToken');
  const removeItem = (basket_item_id, token) => {
    axios.delete(`http://localhost:8000/basket/${basket_item_id}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(response => {
      fetchBasket();
    })
    .catch(error => {
      alert(error.response?.data?.detail || 'Ошибка при удалении элемента');
    });
  };


  if (!user) {
    return (
      <div className="cart-container">
        <h2>Корзина</h2>
        <p>Для просмотра корзины, пожалуйста, войдите в аккаунт.</p>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h2>Корзина</h2>
      {basketItems.length === 0 ? (
        <p>Корзина пуста</p>
      ) : (
        <ul>
          {basketItems.map((item) => (
            <li key={item.basket_item_id}>
              <span>{item.name} - {item.price} руб/месяц</span>
              <button onClick={() => removeItem(item.basket_id)}>Удалить</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Cart;
