import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../App';
import api from '../api/api';

const Login = () => {
  const { saveAuth } = useContext(CartContext);
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    api.post('http://localhost:8000/api/v1/login', { email, password })
      .then(response => {
        const { user, access_token, refresh_token } = response.data;
        localStorage.setItem('accessToken', access_token);  // Сохраняем токен
        localStorage.setItem('refreshToken', refresh_token);
        localStorage.setItem('user', JSON.stringify(user)); // Сохраняем данные пользователя
        saveAuth(user);
        navigate('/');
      })
      .catch(error => {
        alert(error.response.data.detail || 'Ошибка входа');
      });
  };

  return (
    <div className="form-container">
      <h2>Логин</h2>
      <form onSubmit={handleSubmit}>
        <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required />
        <input type="password" placeholder="Пароль" value={password} onChange={e => setPassword(e.target.value)} required />
        <button type="submit">Войти</button>
      </form>
    </div>
  );
};

export default Login;
