import React, {useContext} from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { CartContext } from '../../App';

const AdminHeader = () => {
  const { adminUser, admin_logout } = useContext(CartContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    admin_logout()
    navigate('/');

  };

  return (
    <header>
      <div className="container">
        <nav>
          <div className="logo">
            <Link to="/admin" style={{ color: '#fff', textDecoration: 'none' }}>
              AdminShield
            </Link>
          </div>
          <ul>
            <li><Link to="/admin/products">Продукты</Link></li>
            <li><Link to="/admin/orders">Заказы</Link></li>
            {adminUser ? (
                          <>
                            <li><Link to="/cart">Корзина</Link></li>
                            <li><Link to="/change_password">Сменить пароль</Link></li>
                            <li className="username"> {adminUser.username}</li>
                            <li>
                              <button 
                                onClick={handleLogout} 
                                style={{ background: 'transparent', border: 'none', color: '#fff', cursor: 'pointer' }}>
                                Выход
                              </button>
                            </li>
                          </>
                        ) : (
                          <>
                            <li><Link to="/login">Логин</Link></li>
                            <li><Link to="/register">Регистрация</Link></li>
                          </>
                        )}
            <li>
              <button
                onClick={handleLogout}
                style={{
                  background: 'transparent',
                  border: 'none',
                  color: '#fff',
                  cursor: 'pointer',
                  fontSize: '16px'
                }}
              >
                Выйти
              </button>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default AdminHeader;
